import React, { useState, useEffect } from 'react';
import './App.css';

const specialtiesList = [
  "General Physician", "Dentist", "Dermatologist", "Paediatrician", "Gynaecologist", "ENT",
  "Diabetologist", "Cardiologist", "Physiotherapist", "Endocrinologist", "Orthopaedic",
  "Ophthalmologist", "Gastroenterologist", "Pulmonologist", "Psychiatrist", "Urologist",
  "Dietitian/Nutritionist", "Psychologist", "Sexologist", "Nephrologist", "Neurologist",
  "Oncologist", "Ayurveda", "Homeopath"
];

const App = () => {
  const [doctors, setDoctors] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [consultationType, setConsultationType] = useState('');
  const [selectedSpecialties, setSelectedSpecialties] = useState([]);
  const [sortOption, setSortOption] = useState('');

  useEffect(() => {
    const mockData = [
      { name: "Dr. Munaf Inamdar", specialties: ["General Physician"], experience: 27, fee: 600, consultationTypes: ["Video Consult", "In Clinic"] },
      { name: "Dr. Subhash Bajaj", specialties: ["General Physician", "Cardiologist"], experience: 11, fee: 600, consultationTypes: ["Video Consult"] },
      { name: "Dr. Mufaddal Zakir", specialties: ["General Physician"], experience: 27, fee: 600, consultationTypes: ["In Clinic"] },
      { name: "Dr. Ajay Gangoli", specialties: ["General Physician"], experience: 34, fee: 400, consultationTypes: ["Video Consult", "In Clinic"] },
      { name: "Dr. Devanshi Chaudhary", specialties: ["Dentist"], experience: 5, fee: 300, consultationTypes: ["In Clinic"] },
      { name: "Dr. Devanshi Vaghani", specialties: ["Dentist"], experience: 8, fee: 350, consultationTypes: ["In Clinic"] }
    ];
    setDoctors(mockData);
    setFilteredDoctors(mockData);

    const params = new URLSearchParams(window.location.search);
    setSearchTerm(params.get('search') || '');
    setConsultationType(params.get('consultation') || '');
    setSelectedSpecialties(params.get('specialties')?.split(',') || []);
    setSortOption(params.get('sort') || '');
  }, []);

  useEffect(() => {
    const params = new URLSearchParams();
    if (searchTerm) params.set('search', searchTerm);
    if (consultationType) params.set('consultation', consultationType);
    if (selectedSpecialties.length > 0) params.set('specialties', selectedSpecialties.join(','));
    if (sortOption) params.set('sort', sortOption);
    window.history.pushState({}, '', `${window.location.pathname}?${params.toString()}`);
    applyFilters();
  }, [searchTerm, consultationType, selectedSpecialties, sortOption]);

  const applyFilters = () => {
    let filtered = [...doctors];

    if (searchTerm) {
      filtered = filtered.filter(doctor =>
        doctor.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (consultationType) {
      filtered = filtered.filter(doctor =>
        doctor.consultationTypes.includes(consultationType)
      );
    }

    if (selectedSpecialties.length > 0) {
      filtered = filtered.filter(doctor =>
        doctor.specialties.some(specialty => selectedSpecialties.includes(specialty))
      );
    }

    if (sortOption === 'fees') {
      filtered.sort((a, b) => a.fee - b.fee);
    } else if (sortOption === 'experience') {
      filtered.sort((a, b) => b.experience - a.experience);
    }

    setFilteredDoctors(filtered);
  };

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value) {
      const matches = doctors
        .filter(doctor => doctor.name.toLowerCase().includes(value.toLowerCase()))
        .slice(0, 3);
      setSuggestions(matches);
    } else {
      setSuggestions([]);
    }
  };

  const handleSuggestionClick = (name) => {
    setSearchTerm(name);
    setSuggestions([]);
  };

  const handleConsultationChange = (type) => {
    setConsultationType(type);
  };

  const handleSpecialtyChange = (specialty) => {
    setSelectedSpecialties(prev =>
      prev.includes(specialty)
        ? prev.filter(s => s !== specialty)
        : [...prev, specialty]
    );
  };

  const handleSortChange = (option) => {
    setSortOption(option);
  };

  return (
    <div className="container">
      <div className="search-container">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search doctors..."
          className="search-input"
        />
        {suggestions.length > 0 && (
          <ul className="suggestions-list">
            {suggestions.map((doctor, index) => (
              <li
                key={index}
                onClick={() => handleSuggestionClick(doctor.name)}
                className="suggestion-item"
              >
                {doctor.name}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="main-content">
        <div className="filter-panel">
          <div className="filter-box">
            <h3>Sort by</h3>
            <label>
              <input type="radio" name="sort" value="fees" checked={sortOption === 'fees'} onChange={() => handleSortChange('fees')} />
              Fees: Low-High
            </label><br />
            <label>
              <input type="radio" name="sort" value="experience" checked={sortOption === 'experience'} onChange={() => handleSortChange('experience')} />
              Experience: High-Low
            </label>
          </div>

          <div className="filter-box">
            <h3>Mode of Consultation</h3>
            <label>
              <input type="radio" name="consultation" value="Video Consult" checked={consultationType === 'Video Consult'} onChange={() => handleConsultationChange('Video Consult')} />
              Video Consultation
            </label><br />
            <label>
              <input type="radio" name="consultation" value="In Clinic" checked={consultationType === 'In Clinic'} onChange={() => handleConsultationChange('In Clinic')} />
              In Clinic
            </label>
          </div>

          <div className="filter-box">
            <h3>Specialties</h3>
            <div className="specialties-list">
              {specialtiesList.map(specialty => (
                <label key={specialty}>
                  <input
                    type="checkbox"
                    checked={selectedSpecialties.includes(specialty)}
                    onChange={() => handleSpecialtyChange(specialty)}
                  />
                  {specialty}
                </label>
              ))}
            </div>
          </div>
        </div>

        <div className="doctor-list">
          {filteredDoctors.map((doctor, index) => (
            <div key={index} className="doctor-card">
              <div className="doctor-info">
                <h3>{doctor.name}</h3>
                <p>{doctor.specialties.join(', ')}</p>
                <p>{doctor.experience} yrs exp.</p>
              </div>
              <div>
                <p className="doctor-fee">â‚¹{doctor.fee}</p>
                <button className="book-button">Book Appointment</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
